import React, { useState } from 'react';
import NavBar from '../Navbar/Navbar';
import Sidebar from '../Sidebar/Sidebar';
import popup from '../popupLogin/popup';


import {
	LpContainer,
	LpContent,
	LpItems,
	Lph1,
	Lpp,
	Lpbtn,
} from './HeroElements';

function Hero() {
	const [isOpen, setIsOpen] = useState(false);

	const toggle = () => {
		setIsOpen(!isOpen);
	};

	return (
		<LpContainer>
			<NavBar toggle={toggle} />
			<Sidebar isOpen={isOpen} toggle={toggle} />
			<LpContent>
				<LpItems>
					<Lph1>Secrets to Mouth-watering Recipes</Lph1>
					<Lpp>Get recipes matching your needs in moments!</Lpp>
					<Popup trigger=
                {<button><Lpbtn>Start Now</Lpbtn></button>}
                modal nested>
                {
                    close => (
                        <div className='modal'>
                            <div className='content'>
                                Welcome to GFG!!!
                            </div>
                            <div>
                                <button onClick=
                                    {() => close()}>
                                        Close modal
                                </button>
                            </div>
                        </div>
                    )
                }
            </Popup>
				</LpItems>
			</LpContent>
		</LpContainer>
	);
}

export default Hero;
